import { useState } from 'react';
import { Modal, Button } from 'react-bootstrap';
import { detailFactura } from '../services/productos';

export function Card({ id, title, text }) {
    const [show, setShow] = useState(false);
    const [factureDetail,setFactureDetail]=useState([]);

    let datosInit=[id,[' '],0,0];
    setFactureDetail(datosInit);

    const handleClose = async () => {
        setShow(false);
        detailFactura(id).then(setFactureDetail);
    };
    const handleShow = () => setShow(true);

    return (

        <div className="col-sm-6">
            <div className="card bg-dark">
                <div className="card-body">
                    <h5 className="card-title">{title}</h5>
                    <p className="card-text">Valor total: ${text}</p>
                    <button onClick={handleShow} className="btn btn-primary">Vamos a ver</button>
                </div>
            </div>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>factura de {title}</Modal.Title>
                </Modal.Header>
                <Modal.Body>{factureDetail.ventas} {factureDetail.total} {factureDetail.total_productos}</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>

    );
}
